package Assignment02;

public class DogEx { //클래스명 DogEx로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("|\\_/|"); //역슬래시 \ 출력하기 위해 이스케이프 문자 \\ 사용
		System.out.println("|q p|\t/}"); //강아지 눈과 꼬리 사이를 띄우기 위해 탭 이스케이프 문자 \t 사용
		System.out.println("( 0 )\"\"\"\\"); //강아지 등 큰따옴표를 출력하기 위해 이스케이프 문자 \" 사용, 역슬래시 출력을 위해 \\ 사용
		System.out.println("|\"^\"`\t |"); //큰따옴표 출력을 위해 \" 사용, 탭을 위해 \t 사용
		System.out.println("||_/=\\\\__|"); //역슬래시 출력을 위해 \\ 사용
	}

}
