package Chap02;

public class Datatype01_2 { //클래스명 Datatype01_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10; //int형 변수 a를 10으로 초기화
		short s = 2; //short형 변수 s를 2로 초기화
		byte b = 6; //byte형 변수 b를 6으로 초기화
		long l = 125362133223L; //long형 변수 l을 1235362133223L로 초기화
		System.out.println(a); //int형 변수 a 출력
		System.out.println(s); //short형 변수 s 출력
		System.out.println(b); //byte형 변수 b 출력
		System.out.println(l); //long형 변수 l 출력
	}

}
