package Chap02;

public class Datatype02 { //클래스명 Datatype02로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float f = 65.20298f; //float형 f를 초기화
		double d = 876.765d; //double형 d를 초기화
		System.out.println(f); //f 변수를 출력
		System.out.println(d); //d 변수를 출력
	}

}
