package Chap02;

public class Datatype03 { //클래스명을 Datatype03으로 지정

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'J'; //char형 ch를 초기화
		char ch2 = 'a'; //char형 ch2를 초기화
		char ch3 = 'v'; //char형 ch3를 초기화
		char ch4 = 'a'; //char형 ch4를 초기화
		System.out.println(ch); //ch 변수를 출력
		System.out.println(ch2); //ch2 변수를 출력
		System.out.println(ch3); //ch3 변수를 출력
		System.out.println(ch4); //ch4 변수를 출력
	}

}
