package Chap02;

public class InputOutput01_2 { //클래스명 InputOutput01_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "홍길동"; //String형 변수 myName 초기화
		int myAge = 20; //int형 변수 myAge 초기화
		
		System.out.println("이름: " + myName); //큰따옴표 안의 문자열과 myName 변수 출력
		System.out.println("나이: " + myAge); //큰따옴표 안의 문자열과 myAge 변수 출력
	}

}
