package Chap02;

public class InputOutput02_2 { //클래스명 InputOutput02_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "홍길동"; //String형 변수 myName 초기화
		int myAge = 20; //int형 변수 myAge 초기화
		
		System.out.print("이름: \t" + myName + "\n"); //이름: 뒤에 탭해서 띄우고 myName 변수 출력한 다음 줄 바꿈 
		System.out.print("나이: \t" + myAge + "\n"); //나이: 뒤에 탭해서 띄우고 myAge 변수 출력한 다음 줄 바꿈
	}

}
