package Chap02; //패키지명 Chap02로 선언

public class Variable01_2 { //클래스명 Variable01_2로 선언

	public static void main(String[] args) { //메인 메서드가 문자열 배열을 입력받을 수 있음
		// TODO Auto-generated method stub
		int days = 10; //days 변수를 10(정수)로 초기화
		System.out.println(days); //days 변수 출력
		
		int speed; //speed 변수 정수형으로 선언
		speed = 20; //speed 변수 20으로 초기화
		System.out.println(speed); //speed 변수 출력
	}

}
