package Chap02;

public class Variable02_2 { //클래스명 Variable02_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double PI = 3.14; //PI를 실수형 상수 3.14로 초기화
		System.out.println("반지름 3인 원의 넓이를 구합니다."); //"" 안의 내용 출력
		System.out.print(3 * 3 * PI); //원의 넓이 식의 값을 출력
	}

}
