package Chap03; //패키지명 Chap03으로 선언

public class Example01_2 { //클래스명 Example01_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10, y = 20; //정수형 int 변수 x와 y 선언 및 초기화
		System.out.println("x + y = " + (x + y)); //산술연산자 +를 사용하여 계산한 값과 큰따옴표 안의 문자열 결합하여 출력
		System.out.println("x - y = " + (x - y)); //산술연산자 -를 사용하여 계산한 값과 큰따옴표 안의 문자열 결합하여 출력
		System.out.println("x * y = " + (x * y)); //산술연산자 *를 사용하여 계산한 값과 큰따옴표 안의 문자열 결합하여 출력
		System.out.println("x / y = " + (x / y)); //산술연산자 /를 사용하여 계산한 값(몫)과 큰따옴표 안의 문자열 결합하여 출력
		System.out.println("x % y = " + (x % y)); //산술연산자 %를 사용하여 계산한 값(나머지)과 큰따옴표 안의 문자열 결합하여 출력
	}

}
