package Chap03;

public class Example02_2 { //클래스명 Example02_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10, y = 10; //정수형 변수 x와 y를 10으로 초기화
		System.out.println(x == 10); //관계연산자 ==를 사용해 x와 10이 같은지 물어봄 참이기 때문에 true 출력
		System.out.println("x와 y는 같습니까? " + (x == y)); //큰따옴표 안의 문자열과 x가 y와 같은지의 답인 true를 결합하여 출력 
	}

}
