package Chap03;

public class Example04_2 { //클래스명 Example04_2로 선언

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10; //정수형 변수 x를 10으로 초기화
		System.out.println(x); //변수 x 값 10 출력
		x += 2; //변수 x에 2를 더한 값을 x에 할당
		System.out.println(x); //변수 x 값 12 출력
		x *= 2; //변수 x에 2를 곱한 값을 x에 할당
		System.out.println(x); //변수 x 값 24 출력
		x -= 2; //변수 x에 2를 뺀 값을 x에 할당
		System.out.println(x); //변수 x 값 22 출력
	}

}
