package Chap06;

public class Array05_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "Java Programming"; //스트링 풀(미리 만들어놓은 메모리 공간에서 가져다 씀)
		String s2 = new String("Java Programming"); //스트링을 새로 만들어서 문자열 저장
		
		System.out.println(s1); //문자열 출력
		System.out.println(s2);
	}

}
